mapper.select:
select year
from execute_plan
where id = #{executePlanId,jdbcType=VARCHAR}

select term
from execute_plan
where id = #{executePlanId,jdbcType=VARCHAR}

SELECT DISTINCT clazz
FROM plan
WHERE plan.execute_plan_id = #{executePlanId,jdbcType=VARCHAR}

SELECT course_name
FROM plan
WHERE execute_plan_id = #{executePlanId,jdbcType=VARCHAR}
  AND type = 0
GROUP BY course_name;

SELECT course_name
FROM plan
WHERE execute_plan_id = #{executePlanId,jdbcType=VARCHAR}
  AND type = 1
GROUP BY course_name;

SELECT department.NAME AS departmentName, colleges.NAME AS collegesName, plan.start_pro AS startPro, course_code AS courseCode, course_name AS courseName, book.text_book_name AS textBookName, book.isbn AS isbn, book.text_book_category AS textBookCategory, book.press AS press, book.author AS author, book.unit_price AS unitPrice, book.award_Information AS awardInformation, book.publication_date AS publicationDate, execute_plan.grade AS grade, plan.clazz AS clazz, plan.clazz_number AS clazzNumber, book.teacher_book_number AS teacherBookNumber, book.is_buy_book AS isBuyBook, book.subscriber AS subscriber, book.subscriber_tel AS subscriberTel, book.STATUS AS STATUS, book.is_book_purchase AS isBookPurchase, book.reason AS reason, plan.remark AS remark FROM book INNER JOIN plan_book ON plan_book.book_id = book.id INNER JOIN plan ON plan.id = plan_book.plan_id INNER JOIN execute_plan ON execute_plan.id = plan.execute_plan_id INNER JOIN department ON department.id = execute_plan.department_id INNER JOIN colleges ON plan.colleges_id = colleges.id
plan.colleges_id = #{college,jdbcType=VARCHAR}
and execute_plan.department_id = #{teachingDepartment,jdbcType=VARCHAR}
and execute_plan.`year` = #{year,jdbcType=VARCHAR}
and execute_plan.term = #{term,jdbcType=BIT}
